mapper.select:
  	select P.* from (
	    select rownum rnum, H.* from (
	        select * from w_hospital
	        where h_adcheck='Y'
	        order by h_no desc
	    )H 
	)P
	where rnum between #{startNo} and #{endNo}

  	select * from (
	    select rownum rnum, H.* from(
	        select * from w_hospital
	        where h_adcheck ='Y'
	        
			and h_name like '%'|| #{keyword} || '%'
			
			
			and h_road_address like '%' || #{keyword}  ||'%'
			
	        order by h_no desc
	    )H
	)
	where rnum between #{paging.startNo} and #{paging.endNo}

mapper.update:
update w_hospital
set h_hit = h_hit+1
where h_no = #{hNo}
